import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { TransactionService } from './transaction.service'; // Import the transaction service

@Component({
  templateUrl: './transactionForm.component.html',
})
export class TransactionFormComponent {
  accountId: string;
  amount: number;

  constructor(private transactionService: TransactionService, private router: Router) {
    this.accountId = '';
    this.amount = 0;
  }

  submitTransaction(): void {
    // Create transaction object
    const transaction = { accountId: this.accountId, amount: this.amount };

    // Add transaction to SQLite database
    this.transactionService.addTransaction(transaction).subscribe(() => {
      // Redirect to TransactionListComponent after successful submission
      this.router.navigate(['/transactions']);
    });
  }
}
